mapper.select:
select count(user_id)
from members
where user_id = #{userid}

select user_id userId, user_name userName               
from members                        
where user_id = #{userId} and user_password = #{userPwd}

select user_id userId, user_name userName, user_password userPwd, 
email_id emailId, email_domain emailDomain, join_date joinDate 
from members where user_id=#{userId}
